def compare_configs(old_config, new_config):
    mismatches = []
    for key in old_config:
        if key in new_config:
            if old_config[key] != new_config[key]:
                mismatches.append((key, old_config[key], new_config[key]))
        else:
            mismatches.append((key, old_config[key], 'Key not found in new config'))
    for key in new_config:
        if key not in old_config:
            mismatches.append((key, 'Key not found in old config', new_config[key]))
    return mismatches


def main():
    old_config = {
        "username": "user1",
        "salt": "abc123",
        "password_hash": "hash1",
        "token": "token1"
    }

    new_config = {
        "username": "user1",
        "salt": "abc123",
        "password_hash": "hash2",
        "token": "token2"
    }

    mismatches = compare_configs(old_config, new_config)
    if mismatches:
        print("Mismatches found:")
        for mismatch in mismatches:
            print(f"Key: {mismatch[0]}, Old: {mismatch[1]}, New: {mismatch[2]}")
    else:
        print("No mismatches found.")


if __name__ == "__main__":
    main()

