#!/usr/bin/env python3
"""
Test script to reproduce the issue where Quantity.__array_ufunc__() 
should return NotImplemented instead of raising ValueError.
"""

import dataclasses
import numpy as np
import astropy.units as u


@dataclasses.dataclass
class DuckArray(np.lib.mixins.NDArrayOperatorsMixin):
    ndarray: u.Quantity

    @property
    def unit(self) -> u.UnitBase:
        return self.ndarray.unit

    def __array_ufunc__(self, function, method, *inputs, **kwargs):
        print(f"DuckArray.__array_ufunc__ called with function={function}, method={method}")

        inputs = [inp.ndarray if isinstance(inp, DuckArray) else inp for inp in inputs]

        for inp in inputs:
            if isinstance(inp, np.ndarray):
                result = inp.__array_ufunc__(function, method, *inputs, **kwargs)
                if result is not NotImplemented:
                    return DuckArray(result)

        return NotImplemented


def test_duck_array_operations():
    """Test various operations with DuckArray"""

    print("=== Testing DuckArray operations ===")

    # Test 1: This should work (same units)
    print("\nTest 1: DuckArray(1 * u.mm) + (1 * u.m)")
    try:
        result = DuckArray(1 * u.mm) + (1 * u.m)
        print(f"Success: {result}")
    except Exception as e:
        print(f"Error: {e}")

    # Test 2: This should work (same units, different order)
    print("\nTest 2: (1 * u.mm) + DuckArray(1 * u.mm)")
    try:
        result = (1 * u.mm) + DuckArray(1 * u.mm)
        print(f"Success: {result}")
    except Exception as e:
        print(f"Error: {e}")

    # Test 3: This is the problematic case - should work but currently fails
    print("\nTest 3: (1 * u.m) + DuckArray(1 * u.mm) - PROBLEMATIC CASE")
    try:
        result = (1 * u.m) + DuckArray(1 * u.mm)
        print(f"Success: {result}")
    except Exception as e:
        print(f"Error: {e}")
        print(f"Error type: {type(e)}")


if __name__ == "__main__":
    test_duck_array_operations()
