#!/usr/bin/env python3
"""
Script to reproduce the misleading attribute access message issue in subclassed SkyCoord.

The issue: When a subclassed SkyCoord has a property that tries to access a non-existent 
attribute, the error message incorrectly states that the property doesn't exist instead 
of the actual missing attribute.
"""

import sys
import os

# Add the astropy source to the path so we can import it directly
sys.path.insert(0, '/tmp/tmp_qu0p03y/astropy')

# We need to mock some imports since we can't build the full package
import unittest.mock

# Mock the compiled extensions that we can't build
with unittest.mock.patch.dict('sys.modules', {
    'astropy.utils._compiler': unittest.mock.MagicMock(),
    'astropy._erfa': unittest.mock.MagicMock(),
    'astropy.coordinates.builtin_frames.utils': unittest.mock.MagicMock(),
}):
    try:
        import astropy.coordinates as coord

        class custom_coord(coord.SkyCoord):
            @property
            def prop(self):
                return self.random_attr  # This attribute doesn't exist

        print("Creating custom_coord instance...")
        c = custom_coord('00h42m30s', '+41d12m00s', frame='icrs')

        print("Trying to access c.prop (which will try to access non-existent random_attr)...")
        try:
            result = c.prop
            print(f"Unexpected success: {result}")
        except AttributeError as e:
            print(f"AttributeError caught: {e}")

            # Check if the error message is misleading
            error_msg = str(e)
            if "'prop'" in error_msg:
                print("❌ ISSUE CONFIRMED: Error message mentions 'prop' instead of 'random_attr'")
                print("   This is misleading because 'prop' exists as a property, but 'random_attr' doesn't exist")
            elif "'random_attr'" in error_msg:
                print("✅ ISSUE FIXED: Error message correctly mentions 'random_attr'")
            else:
                print(f"❓ UNEXPECTED: Error message doesn't mention either 'prop' or 'random_attr': {error_msg}")

    except ImportError as e:
        print(f"Import error (expected due to missing compiled extensions): {e}")
        print("Let's try a simpler approach by examining the source code directly...")

        # Let's create a minimal version to test the logic
        class MockSkyCoord:
            def __init__(self):
                self._sky_coord_frame = None

            def __getattr__(self, attr):
                # This is the current problematic implementation
                raise AttributeError(
                    f"'{self.__class__.__name__}' object has no attribute '{attr}'"
                )

        class custom_coord(MockSkyCoord):
            @property
            def prop(self):
                return self.random_attr  # This will trigger __getattr__

        print("\nTesting with mock implementation:")
        c = custom_coord()
        try:
            result = c.prop
        except AttributeError as e:
            print(f"AttributeError: {e}")
            if "'prop'" in str(e):
                print("❌ ISSUE CONFIRMED: Mock shows the same misleading error")
            else:
                print("✅ Different behavior in mock")

